TriggerOraclev10g.body:
DECLARE
    v_count NUMBER;
BEGIN

    SELECT COUNT(*)
    INTO v_count
    FROM vanzari
    WHERE produse_id_p_cr = :NEW.produse_id_p_cr
    AND data = :NEW.data
    AND cantitate_dorita = :NEW.cantitate_dorita
    AND ROWNUM <= 1; 

    IF v_count > 0 THEN
        raise_application_error(-20001, 'Vanzare duplicata.');
    END IF;
END;
